\begin{array}
&amp;\mu_j={\frac{1}{m}\sum_{i=1}^mx_j^{(i)}},&amp;&amp;&amp;\sigma_{j}^{2}=\frac{1}{m}\sum_{i=1}^{m}(x_{j}^{(i)}-\mu_{j})^{2}&amp;
\end{array}

